CREATE OR REPLACE TABLE categories (
  id INT AUTO_INCREMENT PRIMARY KEY,   -- Un identifiant unique pour chaque catégorie
  nom VARCHAR(255) NOT NULL,           -- Le nom de la catégorie
  type ENUM("Catégories de style", "Catégories d'expression", "Catégories de contraintes", "Catégories de structure", "Catégories par ajout") NOT NULL,          -- Le type de la catégorie
  description TEXT,                    -- Une description de la catégorie
  mixte BOOLEAN NOT NULL,              -- Indique si la catégorie ne convient qu'à des improvisation de type mixte
  comparee BOOLEAN NOT NULL            -- Indique si la catégorie ne convient qu'à des improvisation de type comparée
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


('Rimée', 'Catégories d''expression', '', FALSE, FALSE),
('Chantée', 'Catégories d''expression', 'Tout doit être chanté', FALSE, FALSE),
('Comédie musicale', 'Catégories d''expression', 'Du chant, de la danse, des dialogues', FALSE, FALSE),
('Jukebox', 'Catégories d''expression', 'Tous les dialogues doivent être chantés sur plusieurs styles musicaux imposés en indiquant des artistes. Les joueurs devront chanter sur les mélodies de ces chanteurs.', FALSE, FALSE),
('Texte imposé', 'Catégories d''expression', 'Les dialogues de l''un des joueurs proviennent exclusivement d''un texte (de théâtre, ou roman) préparé à l''avance par l''arbitre.', FALSE, FALSE),
('Sans parole', 'Catégories d''expression', 'Tous les bruitages, sons, grognements etc. sont autorisés, mais aucune parole distincte.', FALSE, FALSE),
('Silencieuse', 'Catégories d''expression', 'Aucun son quel qu''il soit n''est autorisé.', FALSE, FALSE),
('Doublage américain', 'Catégories d''expression', 'Des comédiens jouent dans la patinoire, et d''autres font leurs voix.', FALSE, FALSE),
('Grommelot', 'Catégories d''expression', 'Langage inventé, improvisé, dont les mots n''ont de sens que pour l''acteur. Seule l''intonation peut le traduire.', FALSE, FALSE),

('Mimée', 'Catégories de contraintes', '', FALSE, FALSE),
('Muette puis mimée', 'Catégories de contraintes', 'Uniquement en comparée ; une équipe fait une impro muette, l''autre refait la même en mimée.', FALSE, TRUE),
('Croisement', 'Catégories de contraintes', 'Uniquement en mixte avec un joueur par équipe ; les deux joueurs doivent s''échanger leur personnage quand l''arbitre siffle.', TRUE, FALSE),
('Carnage', 'Catégories de contraintes', 'Tous les improvisateurs participant au match doivent mourir avant la fin de l''impro.', FALSE, FALSE),
('Abécédaire', 'Catégories de contraintes', 'La première réplique commence par ''A...'', la seconde par ''B...''. L''impro se termine à ''Z...''.', FALSE, FALSE),
('Switch', 'Catégories de contraintes', 'L''improvisateur qui reçoit un coup de sifflet de l''arbitre doit immédiatement changer le dernier mot, ou la fin de la phrase qu''il a dite.', FALSE, FALSE),
('Zone d''humeurs', 'Catégories de contraintes', 'L''arbitre délimite différentes zones dans la patinoire auxquelles il associe une humeur (joie, tristesse, colère, peur, désir...). En se déplaçant d''une zone à l''autre, les improvisateurs doivent alors adopter l''humeur adéquate. Une humeur peut également être associée au touché du rebord de la patinoire.', FALSE, FALSE),
('Grosse colère', 'Catégories de contraintes', 'Uniquement en mixte avec un joueur par équipe ; les jouteurs commencent l''improvisation de manière cordiale, pour ensuite se disputer, la colère venant crescendo jusqu''à la fin de l''improvisation.', TRUE, FALSE),
('Humeur imposée', 'Catégories de contraintes', 'L''arbitre impose une humeur à chaque improvisateur en jeu, lequel devra donc garder cette humeur tout au long de l''impro. En cas de service, l''arbitre interrompt brièvement l''impro pour imposer une humeur au jouteur.', FALSE, FALSE),

('Poursuite', 'Catégories de structure', 'Uniquement en comparée ; une équipe commence une improvisation que l''autre équipe doit terminer.', FALSE, TRUE),
('Double-poursuite', 'Catégories de structure', 'Uniquement en comparée ; une équipe commence une improvisation que l''autre équipe doit terminer. La première équipe prend la suite de cette deuxième improvisation et la deuxième conclue. 4 improvisations seront jouées au final, constituant une seule et même histoire.', FALSE, TRUE),
('Roman-photo', 'Catégories de structure', 'L''improvisation est jouée en une succession de plans arrêtés (les photos) accompagnées de voix-off faisant parler les personnages.', FALSE, FALSE),
('Fusillade', 'Catégories de structure', 'Lorsque l''arbitre annonce cette catégorie, le nombre de joueurs demandé entre dans la patinoire, après quoi les joueurs font à tour de rôle une improvisation très courte sur un thème, différent pour chaque joueur, sans caucus.', FALSE, FALSE),
('Peau de chagrin', 'Catégories de structure', 'Une même improvisation est jouée plusieurs fois, trois fois la plupart du temps, sur une durée à chaque fois plus courte (en général réduite de moitié).', FALSE, FALSE),
('Exercice de style', 'Catégories de structure', 'Les joueurs vont improviser 5 fois la même impro avec 5 styles imposés par l''arbitre ou le maitre de cérémonie (généralement 5 fois une minute) ex : Péplum, Film d''action, muette, film de Sciences Fictions, Cinéma d''auteur...', FALSE, FALSE),
('Zapping télé', 'Catégories de structure', 'Des groupes de deux improvisateurs font chacun une chaîne ; l''arbitre peut décider de zapper sur une autre chaîne/groupe.', FALSE, FALSE),
('Harold', 'Catégories de structure', 'Tout le monde connaît et parle de Harold, mais personne ne le voit jamais sur scène.', FALSE, FALSE),
('Traveling', 'Catégories de structure', 'Dès qu''un personnage sort de scène, on le suit et l''impro continue sur lui. Le traveling doit suivre chaque sortie de chacun.', FALSE, FALSE),
('Avec exagération', 'Catégories de structure', 'Une même improvisation est jouée trois fois. À chaque fois, les improvisateurs doivent exagérer un peu plus leurs actions, leurs paroles, etc...', FALSE, FALSE),
('Budget dégressif', 'Catégories de structure', 'Une même improvisation, souvent de type film d''action, est jouée deux ou trois fois, avec de moins en moins de budget. Par exemple, un hélicoptère sera remplacé par une moto puis par un vieux vélo, tandis que le jeu des acteurs sera de moins en moins bon.', FALSE, FALSE),
('-1', 'Catégories de structure', 'Une même improvisation est rejouée plusieurs fois, mais, à chaque fois, un joueur est retiré, de manière que les joueurs restants se retrouvent à prendre en charge plusieurs personnages en même temps, et ce jusqu''à ce qu''il ne reste qu''un joueur qui doive rejouer toute l''improvisation seul.', FALSE, FALSE),

('Ambiance musicale', 'Catégories par ajout', 'Le musicien crée une ambiance musicale dont les joueurs doivent prendre compte, à la manière de la bande sonore d''un film.', FALSE, FALSE),
('Accessoire', 'Catégories par ajout', 'L''arbitre donne un objet qui ne devra jamais être utilisé pour sa vraie fonction, et peut changer de fonction en cours d''impro.', FALSE, FALSE);
